import React, { useState, useEffect } from "react";
import { Link, Routes, Route } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import Login from "./components/Login";
import ProjectDetail from "./components/ProjectDetail";
import ContactUs from "./components/ContactUs";
import Task from "./components/Task";
import ProjectManagementUses from "./components/ProjectManagementUses";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [projects, setProjects] = useState([]);
  const [userCredentials, setUserCredentials] = useState({ email: "", password: "" });
  const [backendStatus, setBackendStatus] = useState("Checking...");

  // ✅ Fetch projects from backend when the app loads
  const fetchProjects = async () => {
    try {
      const response = await fetch("http://localhost:9004/api/projects");
      if (!response.ok) throw new Error(`Failed to fetch projects: ${response.status}`);
      const data = await response.json();
      console.log("📥 Projects Fetched:", data);
      if (Array.isArray(data)) {
        setProjects(data);
      } else {
        console.error("❌ Invalid project format:", data);
      }
    } catch (error) {
      console.error("❌ Error fetching projects:", error);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  // ✅ Check backend connection when the app loads
  useEffect(() => {
    fetch("http://localhost:9004/api/test")
      .then((response) => response.json())
      .then((data) => setBackendStatus(data.message ? `✅ Backend Connected: ${data.message}` : "❌ Backend Response Missing 'message' Key"))
      .catch((error) => {
        console.error("❌ Backend connection failed:", error);
        setBackendStatus("❌ Backend Not Connected");
      });
  }, []);

  const handleLogin = (email, password) => {
    setUserCredentials({ email, password });
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserCredentials({ email: "", password: "" });
  };

  // ✅ Function to add a project
  const addProject = async (newProject) => {
    try {
      const response = await fetch("http://localhost:9004/api/project", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProject),
      });

      if (!response.ok) {
        throw new Error(`Failed to add project: ${response.status}`);
      }

      const savedProject = await response.json();
      console.log("✅ Project Added:", savedProject);
      setProjects((prevProjects) => [...prevProjects, savedProject]); // ✅ Update state
    } catch (error) {
      console.error("❌ Error adding project:", error);
    }
  };

  const deleteProject = async (projectId) => {
    try {
      const response = await fetch(`http://localhost:9004/api/project/${projectId}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        throw new Error(`Failed to delete project: ${response.status}`);
      }

      console.log("✅ Project Deleted");
      setProjects((prevProjects) => prevProjects.filter((project) => project._id !== projectId));
    } catch (error) {
      console.error("❌ Error deleting project:", error);
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} showLoginButton={!isAuthenticated} />

      <div className="flex flex-1">
        <Sidebar projects={projects} onAddProject={addProject} onDeleteProject={deleteProject} />

        <div className="flex-1 p-6">
          <Toaster position="top-right" gutter={8} />

          <div className="p-4 bg-gray-100 text-center text-lg font-semibold">
            {backendStatus}
          </div>

          <Routes>
            <Route path="/project/:projectId" element={isAuthenticated ? <ProjectDetail projects={projects} /> : <Login onLogin={handleLogin} />} />
            <Route path="/" element={isAuthenticated ? <Task /> : <p className="text-center">Please log in to see projects.</p>} />
            <Route path="/contact-us" element={<ContactUs />} />
            <Route path="/project-management-uses" element={<ProjectManagementUses />} />
          </Routes>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default App;
