import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Sidebar = ({ projects, onAddProject, onDeleteProject }) => {
  const [newProjectName, setNewProjectName] = useState("");
  const [newProjectDescription, setNewProjectDescription] = useState("");

  useEffect(() => {
    console.log("üìú Sidebar Updated - Projects Received:", projects);
  }, [projects]);

  const handleAddProject = async () => {
    if (!newProjectName.trim() || !newProjectDescription.trim()) {
      alert("‚ö†Ô∏è Please enter a valid project name and description.");
      return;
    }

    const newProject = {
      title: newProjectName.trim(),
      description: newProjectDescription.trim(),
    };

    try {
      await onAddProject(newProject); // ‚úÖ Add project to backend
      setNewProjectName("");
      setNewProjectDescription("");
    } catch (error) {
      console.error("‚ùå Error adding project:", error);
    }
  };

  const handleDeleteProject = async (projectId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this project?");
    if (confirmDelete) {
      try {
        await onDeleteProject(projectId); // ‚úÖ Delete project from backend
      } catch (error) {
        console.error("‚ùå Error deleting project:", error);
      }
    }
  };

  return (
    <div className="w-64 bg-gray-800 text-white p-6">
      <h2 className="text-lg font-semibold mb-4">Projects</h2>

      <p className="text-gray-400 mb-2">Total Projects: {projects.length}</p>

      <ul className="mb-4">
        {projects.length > 0 ? (
          projects.map((project) => (
            <li key={project._id} className="flex justify-between items-center mb-2">
              <Link
                to={`/project/${project._id}`}
                className="block py-2 px-4 bg-gray-700 hover:bg-gray-600 rounded flex-1 truncate"
              >
                {project.title}
              </Link>
              <button
                onClick={() => handleDeleteProject(project._id)}
                className="ml-2 px-2 py-1 text-red-500 hover:text-red-700"
                title="Delete Project"
              >
                ‚ùå
              </button>
            </li>
          ))
        ) : (
          <p className="text-gray-400">No projects found.</p>
        )}
      </ul>

      {/* Add New Project */}
      <h3 className="text-lg font-semibold mb-3">Add New Project</h3>
      <input
        type="text"
        placeholder="Project Name"
        value={newProjectName}
        onChange={(e) => setNewProjectName(e.target.value)}
        className="w-full p-2 mb-2 text-black rounded"
      />
      <textarea
        placeholder="Project Description"
        value={newProjectDescription}
        onChange={(e) => setNewProjectDescription(e.target.value)}
        className="w-full p-2 mb-2 text-black rounded"
      />
      <button
        onClick={handleAddProject}
        className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded transition"
      >
        ‚ûï Add Project
      </button>
    </div>
  );
};

export default Sidebar;
