import React, { useState, useEffect } from "react";
import { Link, Routes, Route } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import Login from "./components/Login";
import ProjectDetail from "./components/ProjectDetail";
import ContactUs from "./components/ContactUs";
import Task from "./components/Task";
import ProjectManagementUses from "./components/ProjectManagementUses";
import { getProjects, addProject } from "./api/projectApi"; // Ensure projectApi.js has correct functions

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [projects, setProjects] = useState([]);
  const [userCredentials, setUserCredentials] = useState({ email: "", password: "" });
  const [backendStatus, setBackendStatus] = useState("Checking...");

  // ‚úÖ Fetch projects from backend
  const fetchProjects = async () => {
    try {
      console.log("üîÑ Fetching projects...");
      const data = await getProjects();
      if (Array.isArray(data)) {
        setProjects(data);
        console.log("üì• Projects Loaded:", data);
      } else {
        console.error("‚ùå Unexpected data format:", data);
      }
    } catch (error) {
      console.error("‚ùå Error fetching projects:", error);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  // ‚úÖ Check backend connection
  useEffect(() => {
    fetch("http://localhost:9004/api/test")
      .then((response) => {
        if (!response.ok) throw new Error(`Failed to fetch: ${response.status}`);
        return response.json();
      })
      .then((data) => {
        console.log("üì• Backend Response:", data);
        setBackendStatus(data.message ? `‚úÖ Backend Connected: ${data.message}` : "‚ùå Backend Response Missing 'message' Key");
      })
      .catch((error) => {
        console.error("‚ùå Backend connection failed:", error);
        setBackendStatus("‚ùå Backend Not Connected");
      });
  }, []);

  const handleLogin = (email, password) => {
    setUserCredentials({ email, password });
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserCredentials({ email: "", password: "" });
  };

  // ‚úÖ Function to add a project
  const addNewProject = async (newProject) => {
    try {
      console.log("üì§ Sending new project:", newProject);
      const savedProject = await addProject(newProject);
      console.log("‚úÖ Project Added:", savedProject);

      // Ensure new project appears in frontend
      setProjects((prevProjects) => [...prevProjects, savedProject]);
    } catch (error) {
      console.error("‚ùå Error adding project:", error);
    }
  };

  // ‚úÖ Function to delete a project
  const deleteProject = async (projectId) => {
    try {
      console.log(`üóë Deleting project with ID: ${projectId}`);
      const response = await fetch(`http://localhost:9004/api/project/${projectId}`, {
        method: "DELETE",
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete project: ${response.status} - ${errorText}`);
      }

      console.log("‚úÖ Project Deleted Successfully");
      
      // Remove deleted project from state
      setProjects((prevProjects) => prevProjects.filter((project) => project._id !== projectId));
    } catch (error) {
      console.error("‚ùå Error deleting project:", error);
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} showLoginButton={!isAuthenticated} />

      <div className="flex flex-1">
        <Sidebar projects={projects} onAddProject={addNewProject} onDeleteProject={deleteProject} />

        <div className="flex-1 p-6">
          <Toaster position="top-right" gutter={8} />

          {/* ‚úÖ Show Backend Connection Status */}
          <div className="p-4 bg-gray-100 text-center text-lg font-semibold">
            {backendStatus}
          </div>

          <Routes>
            <Route path="/project/:projectId" element={isAuthenticated ? <ProjectDetail projects={projects} /> : <Login onLogin={handleLogin} />} />
            <Route
              path="/"
              element={
                isAuthenticated ? (
                  <Task />
                ) : (
                  <div className="text-center">
                    <h2 className="text-2xl font-semibold mb-4">Welcome to Project Manager</h2>
                    <p className="mb-6 text-gray-600">
                      Get started by managing your projects here. You can view, create, and track the progress of all your projects.
                    </p>
                    <p className="text-lg">
                      Please select or create a new project. To start managing projects, you need to log in first.
                    </p>

                    <div className="mt-6">
                      <Link to="/project-management-uses" className="text-blue-400 hover:text-blue-600">
                        Learn About Project Management Uses
                      </Link>
                    </div>
                  </div>
                )
              }
            />
            <Route path="/contact-us" element={<ContactUs />} />
            <Route path="/project-management-uses" element={<ProjectManagementUses />} />
          </Routes>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default App;
