import React, { useState, useEffect } from "react";
import { Link, Routes, Route, useNavigate } from "react-router-dom"; // ✅ Import useNavigate
import { Toaster } from "react-hot-toast";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import Login from "./components/Login";
import ProjectDetail from "./components/ProjectDetail";
import ContactUs from "./components/ContactUs";
import Task from "./components/Task";
import ProjectManagementUses from "./components/ProjectManagementUses";
import { addProject, getProjects } from "./api/projectApi.js";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [projects, setProjects] = useState([]);
  const navigate = useNavigate(); // ✅ Use navigation

  useEffect(() => {
    getProjects()
      .then((data) => setProjects(data))
      .catch((error) => console.error("❌ Error fetching projects:", error));
  }, []);

  // ✅ Handle user login
  const handleLogin = (email, password) => {
    console.log("🔑 Logging in user:", email); // ✅ Debugging
    setIsAuthenticated(true);
    navigate("/"); // ✅ Redirect to home after login
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header isAuthenticated={isAuthenticated} onLogout={() => setIsAuthenticated(false)} showLoginButton={!isAuthenticated} />

      <div className="flex flex-1">
        <Sidebar projects={projects} />

        <div className="flex-1 p-6">
          <Toaster position="top-right" gutter={8} />

          <Routes>
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/project/:projectId" element={isAuthenticated ? <ProjectDetail projects={projects} /> : <Login onLogin={handleLogin} />} />
            <Route path="/" element={isAuthenticated ? <Task /> : <Login onLogin={handleLogin} />} />
            <Route path="/contact-us" element={<ContactUs />} />
            <Route path="/project-management-uses" element={<ProjectManagementUses />} />
          </Routes>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default App;
