import React, { useState } from "react";
import { Link } from "react-router-dom";

const Sidebar = ({ projects, onAddProject, onDeleteProject }) => {
  const [newProjectName, setNewProjectName] = useState("");
  const [newProjectDescription, setNewProjectDescription] = useState("");
  const [error, setError] = useState(""); // ✅ Error handling for empty fields

  const handleAddProject = () => {
    if (!newProjectName.trim() || !newProjectDescription.trim()) {
      setError("⚠️ Project Name & Description cannot be empty.");
      return;
    }

    const newProject = { title: newProjectName, description: newProjectDescription };
    onAddProject(newProject);
    
    // ✅ Reset form & clear errors after adding
    setNewProjectName("");
    setNewProjectDescription("");
    setError("");
  };

  const handleDelete = (projectId) => {
    if (window.confirm("Are you sure you want to delete this project?")) {
      onDeleteProject(projectId);
    }
  };

  return (
    <div className="w-64 bg-gray-800 text-white p-6">
      <h2 className="text-lg font-semibold mb-6">Projects</h2>
      <ul>
        {projects.length > 0 ? (
          projects.map((project) => (
            <li key={project._id} className="flex justify-between items-center">
              <Link to={`/project/${project._id}`} className="block py-2 px-4 hover:bg-gray-700 rounded">
                {project.title}
              </Link>
              <button
                onClick={() => handleDelete(project._id)}
                className="ml-2 text-red-500 hover:text-red-700"
              >
                Delete
              </button>
            </li>
          ))
        ) : (
          <p className="text-gray-400">No projects found.</p>
        )}
      </ul>

      {/* ✅ Add New Project */}
      <div className="mt-6">
        <h3 className="text-lg font-semibold mb-4">Add New Project</h3>
        {error && <p className="text-red-400">{error}</p>} {/* ✅ Show error if fields are empty */}
        <input
          type="text"
          placeholder="Project Name"
          value={newProjectName}
          onChange={(e) => setNewProjectName(e.target.value)}
          className="w-full p-2 mb-2 text-black rounded"
        />
        <textarea
          placeholder="Project Description"
          value={newProjectDescription}
          onChange={(e) => setNewProjectDescription(e.target.value)}
          className="w-full p-2 mb-2 text-black rounded"
        />
        <button
          onClick={handleAddProject}
          className="w-full bg-blue-500 text-white py-2 rounded mt-2 hover:bg-blue-600"
        >
          Add Project
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
